.hero {
background: linear-gradient(135deg, #2d1b69 0%, #1a1033 100%);
color: white;
padding: 4rem 1rem;
text-align: center;
border-radius: 8px;
margin: 2rem 0;
}

.hero-logo {
max-width: 200px;
height: auto;
margin-bottom: 1.5rem;
}

.tagline {
font-size: 1.5rem;
margin-bottom: 2rem;
color: #e6d5ff;
}

.cta-buttons {
display: flex;
gap: 1rem;
justify-content: center;
margin-top: 2rem;
}

.btn {
padding: 0.75rem 1.5rem;
border-radius: 4px;
text-decoration: none;
font-weight: 600;
transition: all 0.3s ease;
}

.btn-primary {
background-color: #8a63d2;
color: white;
border: 2px solid #8a63d2;
}

.btn-primary:hover {
background-color: #7a53c2;
border-color: #7a53c2;
}

.btn-secondary {
background-color: transparent;
color: white;
border: 2px solid white;
}

.btn-secondary:hover {
background-color: rgba(255, 255, 255, 0.1);
}

.pillars {
display: grid;
grid-template-columns: repeat(auto-fit, minmax(250px, 1fr));
gap: 1.5rem;
margin: 3rem 0;
}

.pillar {
background: #f8f9fa;
border-radius: 8px;
padding: 1.5rem;
transition: transform 0.3s ease, box-shadow 0.3s ease;
border: 1px solid #e9ecef;
}

.pillar:hover {
transform: translateY(-5px);
box-shadow: 0 10px 20px rgba(0, 0, 0, 0.1);
}

.pillar h3 {
color: #2d1b69;
margin-top: 0;
}

.btn-pillar {
color: #8a63d2;
text-decoration: none;
font-weight: 600;
display: inline-block;
margin-top: 1rem;
}

.quick-links {
display: grid;
grid-template-columns: repeat(auto-fit, minmax(250px, 1fr));
gap: 1.5rem;
margin: 3rem 0;
}

.card {
background: #f8f9fa;
border-radius: 8px;
padding: 1.5rem;
text-decoration: none;
color: inherit;
transition: transform 0.3s ease, box-shadow 0.3s ease;
border: 1px solid #e9ecef;
display: block;
}

.card:hover {
transform: translateY(-5px);
box-shadow: 0 10px 20px rgba(0, 0, 0, 0.1);
color: #2d1b69;
}

.card h3 {
margin-top: 0;
color: #2d1b69;
}

@media (max-width: 768px) {
.pillars, .quick-links {
grid-template-columns: 1fr;
}

.cta-buttons {
flex-direction: column;
align-items: center;
}

.btn {
width: 100%;
max-width: 250px;
margin: 0.5rem 0;
}
}

